package org;

public class IfConstructExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b;
		a=400;
		b=200;
		// conditional construct
		if(a>b) {
			System.out.println("A is greater than B");
		}
		else
		{
			System.out.println("B is greater than A");
		}
	}

}
