package org;

public class IfConstructExample2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		if(true) {
			System.out.println("I am always true");
		}
		else
		{
			System.out.println("I am always false");
		}
		
		if(false) {
			System.out.println("I am always true");
		}
		else
		{
			System.out.println("I am always false");
		}
	}

}
